import React from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';
export default class Welcome extends React.Component {
  render() { 
        return(
    <View style={styles.container}>
      <Image source={require('./img/home.jpg')} style={styles.logo} />
      <Text style={styles.title}>Welcome to BKSport!</Text>
      <Text style={styles.subtest}>Make it more convenient for you to book a pitch</Text>
      <TouchableOpacity onPress={() =>
            this.props.navigation.navigate('Weather')
          } style={styles.button1}>
        <Text style={styles.buttonText1}>Login</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button2}>
        <Text style={styles.buttonText2}>Sign up</Text>
      </TouchableOpacity>
    </View>
  );
  }
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 26,
    fontWeight: 'bold',
    marginBottom: 20,
    marginVertical:20,
    marginTop:15,

  },   
  logo: {
    marginTop:20,
    width: 400,
    height: 250,
    marginBottom: 50,
  },
  subtest:{
    fontSize: 16,
    textAlign: 'center',
    marginHorizontal: 30,
    marginBottom: 30,
    marginTop:10,
  },
  button1: {
    marginTop:70,
    backgroundColor: '#F47229',
    padding: 10,
    width: 181.28,
    height: 48,
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 8,
    paddingVertical: 10,
    paddingHorizontal: 49,
    gap: 10,
  },
  button2: {
    marginTop:40,
    backgroundColor: '#FFFFFF',
    padding: 10,
    width: 181.28,
    height: 48,
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 8,
    paddingVertical: 10,
    paddingHorizontal: 49,
    gap: 10,
    borderColor:'#F47229',
    borderWidth:1,
  },
  buttonText1: {
    color: '#FFFFFF',
    fontSize: 16,
    fontWeight: 'bold',
  },
  buttonText2: {
    color: '#000000',
    fontSize: 16,
    fontWeight: 'bold',
  },
});
